Map::Map(){
    floor1 = textureManager::loadTexture("assest_img/floor_1.png");
    floor2 = textureManager::loadTexture("assest_img/floor_2.png");
    floor3 = textureManager::loadTexture("assest_img/floor_3.png");
    floor4 = textureManager::loadTexture("assest_img/floor_4.png");

    loadMap(lvl_1);

    src.x = src.y = 0;
    src.w = dest.w = 32;
    src.h = dest.h = 32;
    dest.x = dest.y = 0;
}

Map::~Map(){
    SDL_DestroyTexture(floor2);
    SDL_DestroyTexture(floor3);
    SDL_DestroyTexture(floor4);
}

void Map::loadMap(int a[100][100]){
    for(int row=0; row<100; row++){
        for(int col=0; col<100; col++){
            gameMap[row][col] = a[row][col];
        }
    }
}

void Map::drawMap(){
    int type = 0;
    for(int row=0; row<100; row++){
        for(int col=0; col<100; col++){
            type = gameMap[row][col];

            dest.x = col*32;
            dest.y = row*32;
            switch (type)
            {
            case 1:
                textureManager::draw(floor1, src, dest);
                break;
            case 2:
                textureManager::draw(floor2, src, dest);
                break;
            case 3:
                textureManager::draw(floor3, src, dest);
                break;
            case 4:
                textureManager::draw(floor4, src, dest);
                break;
            default:
                break;
            }
        }
    }
}
